import http from "./axiosHttp";

const getAll = () => {
    return http.get("/categorys");
};

const get = id_ct => {
    return http.get(`/categorys/${id_ct}`);
};

const create = data => {
    return http.post("/categorys", data);
};

const update = (id_ct, data) => {
    return http.put(`/categorys/${id_ct}`, data);
};

const remove = id_ct => {
    console.log(id_ct);
    return http.delete(`/categorys/${id_ct}`);
};


export default {
    getAll,
    get,
    create,
    update,
    remove,
};
