import React, { useContext, useEffect, useState } from 'react'
import { DataContext } from '../CartControl'

const Cart = props => {
    const value = useContext(DataContext)
    const [cart, setCart] = value.cart;
    const [total, setTotal] = useState(0)

    useEffect(() => {
        const getTotal = () => {
            const res = cart.reduce((prev, item) => {
                return prev + (item.price * item.count)
            }, 0)
            setTotal(res)
        }
        getTotal()
    }, [cart])

    const reduction = id => {
        cart.forEach(item => {
            if (item.id === id) {
                item.count === 1 ? item.count = 1 : item.count -= 1;
            }
        })
        setCart([...cart])
    }
    const increase = id => {
        cart.forEach(item => {
            if (item.id === id) {
                item.count += 1;
            }
        })
        setCart([...cart])
    }

    const removeProduct = id => {
        if (window.confirm("Bạn có muốn xóa sản phẩm")) {
            cart.forEach((item, index) => {
                if (item.id === id) {
                    cart.splice(index, 1)
                }
            })
            setCart([...cart])
        }
    }


    if (cart.length === 0)
        return

    return (
        <div>
            <div className="container">
                <div className="check">
                    <h1>My Shopping Bag ({cart.length})</h1>
                    <div className="col-md-9 cart-items">
                        <div className="cart-header">

                            {
                                cart.map(item, index => (
                                    <div className="cart-sec simpleCart_shelfItem" key={index}>
                                        <div className="cart-item cyc">
                                            <img src={item.anh} className="img-responsive" alt="" />
                                        </div>
                                        <div className="cart-item-info">
                                            <h3><a href="#">{item.ten_sp}</a></h3>
                                            <ul className="qty">
                                                <li><p>Size : </p></li>
                                                <li><p>Màu : 1</p></li>
                                            </ul>
                                            <div className="delivery">
                                                <p>Giá : {item.gia_ban * item.count}</p>
                                                {/* <span>Delivered in 2-3 bussiness days</span> */}
                                                <div className="clearfix" />
                                            </div>

                                            <div className="amount">
                                                <button className="count" onClick={() => reduction(item.id)}> - </button>
                                                <span>{item.count}</span>
                                                <button className="count" onClick={() => increase(item.id)}> + </button>
                                            </div>
                                        </div>
                                        <div className="close1" onClick={() => removeProduct(item.id)}> </div>
                                    </div>
                                ))
                            }


                            <div className="clearfix" />
                        </div>
                    </div>
                    <div className="col-md-3 cart-total">
                        <h1>Tóm tắt đơn hàng</h1>
                        <div className="price-details">
                            <h3>Tổng đơn hàng</h3>
                            <span>Giá: </span>
                            <span className="total1">6200.00</span>
                            <div className="clearfix" />
                        </div>
                        <ul className="total_price">
                            <li className="last_price"> <h4>Tổng :</h4></li>
                            <li className="last_price"><span>6350.00</span></li>
                            <div className="clearfix"> </div>
                        </ul>
                        <div className="clearfix" />
                        <a className="order" href="#">Thanh Toán</a>
                        <div className="total-item">
                            <h3>Chúng tôi tiếp nhận</h3>
                            <a className="cpns" href="#">Apply Coupons</a>
                            <p><a href="#">Log In</a> to use accounts - linked coupons</p>
                        </div>
                    </div>
                    <div className="clearfix"> </div>
                </div>
            </div>
        </div>
    )
}

Cart.propTypes = {

}

export default Cart
